            function hideFooterPopup(popupWrapper, popup) {
                popupWrapper.classList.remove('show');
                popup.classList.remove('show');
            }

            function showFooterPopup(popupWrapper, popup) {
                popupWrapper.classList.add('show');
                popup.classList.add('show');
            }

            function addFooterPopup(wrapperClass, linkId) {
                const popupWrapper = document.querySelector(wrapperClass);
                const link = document.getElementById(linkId);
                if (!popupWrapper || !link) return;
                const popupClose = popupWrapper.querySelector('.dashboard-footer-popup-close');
                const popupCloseD = popupWrapper.querySelector('.form-close');
                const popup = popupWrapper.querySelector('.dashboard-footer-popup');
                const popupCloseArea = popupWrapper.querySelector('.dashboard-footer-popup-close-area');

                link.addEventListener('click', () => {
                    showFooterPopup(popupWrapper, popup);
                });
                [popupClose, popupCloseD, popupCloseArea].forEach(p => {
                    p.addEventListener('click', () => {
                        hideFooterPopup(popupWrapper, popup);
                    });
                });
            }

            function startFooterPopups() {
                addFooterPopup('.home__contact-popup', 'footer-contact');
                addFooterPopup('.home__disclaimer-popup', 'footer-disclaimer');
            }
            startFooterPopups();

            function subscribePopupFooter() {
                const popup = document.getElementById('subscribe-popup');
                const btn = document.getElementById('footer-subscribe');
                if (!popup || !btn) return;
                btn.addEventListener('click', () => {
                    popup.classList.remove('hide');
                    popup.classList.add('show');
                });
            }
            subscribePopupFooter();
        </script>
