<script>
    window.Webflow = window.Webflow || [];
    window.Webflow.push(() => {
        $(".popup__content").on("click", (e) => {
            const $target = $(e.target);
            const $popupContent = $(e.currentTarget);
            const $popupContentChildren = $popupContent.children();
            const $closeBtn = $popupContent.parent().find(".popup__close");

            if ($target.is($popupContentChildren)) return;
            if ($target.closest($popupContentChildren).length > 0) return;

            $closeBtn[0].click();
        });
    });

</script>
