<script>
    function setCookie(name,value,days) {
        var expires = "";
        if (days) {
            var date = new Date();
            date.setTime(date.getTime() + (days*24*60*60*1000));
            expires = "; expires=" + date.toUTCString();
        }
        document.cookie = name + "=" + (JSON.stringify(value) || "")  + expires + "; path=/";
    }

    function getCookie(name) {
        var nameEQ = name + "=";
        var ca = document.cookie.split(';');
        for(var i=0;i < ca.length;i++) {
            var c = ca[i];
            while (c.charAt(0)==' ') c = c.substring(1,c.length);
            if (c.indexOf(nameEQ) == 0) return JSON.parse(c.substring(nameEQ.length,c.length));
        }
        return null;
    }
</script>

<script>
    function verifyPrivacyPolicy() {
        const v = getCookie('privacy-policy-alert');
        if (v) {
            setCookie('privacy-policy-alert', 'true', 30);
            window.localStorage.setItem('fbGrantConsent', 'true');
        } else {
            const ppp = document.querySelector('.privacy-policy-popup');
            if (!ppp) return;
            ppp.classList.add('show');

            const closeEl = ppp.querySelector('.privacy-policy-popup-close');
            if (closeEl) closeEl.addEventListener('click', () => {
                setCookie('privacy-policy-alert', 'true', 30);
                window.localStorage.setItem('fbGrantConsent', 'true');
                ppp.classList.remove('show');
            });
        }
    }
    window.setTimeout(verifyPrivacyPolicy,1000);
</script>



