<script async type="text/javascript" src="https://static.klaviyo.com/onsite/js/klaviyo.js?company_id=Y87gzZ"></script>
<script>
    window.subscribePopupEl = document.getElementById('subscribe-popup');

    function sendKlaviyoForm() {
        const klaviyoForm = document.getElementById('klaviyo-form');
        const inputEl = klaviyoForm.querySelector('input');
        const buttonEl = klaviyoForm.querySelector('button');
        const email = (document.getElementById('email-new-popup')).value;

        const nativeTextAreaValueSetter = Object.getOwnPropertyDescriptor(window.HTMLInputElement.prototype, 'value').set;
        if (nativeTextAreaValueSetter) nativeTextAreaValueSetter.call(inputEl, email);

        const inputEvent = new Event('input', { bubbles: true });
        inputEl.dispatchEvent(inputEvent);

        subscribePopupEl.querySelector('form').style.display = 'none';
        subscribePopupEl.querySelector('.subscribe-popup-form-success').style.display = 'block';

        window.setTimeout(() => {
            const clickEvent = new Event('click', { bubbles: true });
            buttonEl.dispatchEvent(clickEvent);
        }, 1000);

        window.localStorage.setItem('home-subscribed', 'true');
    }

    function closeNewFormPopup() {
        subscribePopupEl.classList.remove('show');
        subscribePopupEl.classList.add('hide');
    }

    function updateNewFormPopup() {
        const formEl = document.getElementById('form-popup-new');
        formEl.action = '';
        formEl.onsubmit = (ev) => {
            ev.preventDefault();
            ev.stopPropagation();
        }
        const formElClone = formEl.cloneNode(true);
        formEl.parentElement.prepend(formElClone);
        formEl.remove();

        subscribePopupEl.addEventListener('click', e => {
            if (e.target === e.currentTarget) closeNewFormPopup();
        });
        subscribePopupEl.querySelector('.subscribe-popup-close').addEventListener('click', () => { closeNewFormPopup(); });

        formElClone.querySelector('input[type="submit"]').addEventListener('click', e => {
            e.preventDefault();
            e.stopPropagation();

            if (!formElClone.querySelector('input[type="email"]').reportValidity()) return;
            sendKlaviyoForm();
        })
    }
    updateNewFormPopup();

    window.setTimeout(() => {
        if (subscribePopupEl && window.localStorage.getItem('home-subscribed') !== 'true') {
            subscribePopupEl.classList.add('show');
            /* window.setTimeout(() => {
              subscribePopupEl.classList.remove('show');
            }, 10000); */
        }
    }, 6000);
</script></body></html>
